    public static boolean checkDateFormat(String input) {
        Pattern ptr = Pattern.compile("^[0-9]{2}\\/[0-9]{2}\\/[0-9]{4}$");
        return ptr.matcher(input).matches();
    }

    public static boolean checkIntegerNumberFormat(String input) {
        Pattern ptr = Pattern.compile("^\\-{0,1}[0-9]{1,}$");
        return ptr.matcher(input).matches();
    }

    public static boolean checkDoubleNumberFormat(String input) {
        Pattern ptr = Pattern.compile("^\\-{0,1}[0-9]{1,}(\\.{1}[0-9]{1,}){0,1}$");
        return ptr.matcher(input).matches();
    }

    public static Date[] generateTimeRange(int timeRangeIndex, String fromDateString, String toDateString) {
        DateFormat dfDate = new SimpleDateFormat("dd/MM/yyyy");
        Date fromDate = new Date(), toDate = new Date();
        switch (timeRangeIndex) {
            case 1: {
                Calendar calFrom = Calendar.getInstance();
                Calendar calTo = Calendar.getInstance();

                calFrom.add(Calendar.DATE, -1);
                calFrom.set(Calendar.HOUR, 0);
                calFrom.set(Calendar.MINUTE, 0);
                calFrom.set(Calendar.SECOND, 0);
                fromDate = calFrom.getTime();

                calTo.add(Calendar.DATE, -1);
                calTo.set(Calendar.HOUR, 23);
                calTo.set(Calendar.MINUTE, 59);
                calTo.set(Calendar.SECOND, 59);
                toDate = calTo.getTime();
                break;
            }
            case 2: {
                Calendar calFrom = Calendar.getInstance();
                Calendar calTo = Calendar.getInstance();

                calFrom.add(Calendar.DATE, -7);
                calFrom.set(Calendar.HOUR, 0);
                calFrom.set(Calendar.MINUTE, 0);
                calFrom.set(Calendar.SECOND, 0);
                fromDate = calFrom.getTime();

                calTo.set(Calendar.HOUR, 23);
                calTo.set(Calendar.MINUTE, 59);
                calTo.set(Calendar.SECOND, 59);
                toDate = calTo.getTime();
                break;
            }
            case 3: {
                Calendar calFrom = Calendar.getInstance();
                Calendar calTo = Calendar.getInstance();

                calFrom.set(Calendar.DAY_OF_MONTH, 1);
                calFrom.set(Calendar.HOUR, 0);
                calFrom.set(Calendar.MINUTE, 0);
                calFrom.set(Calendar.SECOND, 0);
                fromDate = calFrom.getTime();

                calTo.set(Calendar.HOUR, 23);
                calTo.set(Calendar.MINUTE, 59);
                calTo.set(Calendar.SECOND, 59);
                toDate = calTo.getTime();
                break;
            }
            case 4: {
                if (!CommonUltilities.checkDateFormat(fromDateString) || !CommonUltilities.checkDateFormat(toDateString)) {
                    return null;
                }

                try {
                    Calendar calFrom = Calendar.getInstance();
                    Calendar calTo = Calendar.getInstance();

                    calFrom.setTime(dfDate.parse(fromDateString));
                    calFrom.set(Calendar.HOUR, 0);
                    calFrom.set(Calendar.MINUTE, 0);
                    calFrom.set(Calendar.SECOND, 0);
                    fromDate = calFrom.getTime();

                    calTo.setTime(dfDate.parse(toDateString));
                    calTo.set(Calendar.HOUR, 23);
                    calTo.set(Calendar.MINUTE, 59);
                    calTo.set(Calendar.SECOND, 59);
                    toDate = calTo.getTime();

                    if (calFrom.after(calTo)) {
                        return null;
                    }
                } catch (ParseException ex) {
                    Logger.getLogger(CommonUltilities.class.getName()).log(Level.SEVERE, null, ex);
                }
                break;
            }
            default: {
                Calendar calFrom = Calendar.getInstance();
                Calendar calTo = Calendar.getInstance();

                calFrom.set(Calendar.HOUR, 0);
                calFrom.set(Calendar.MINUTE, 0);
                calFrom.set(Calendar.SECOND, 0);
                fromDate = calFrom.getTime();

                calTo.set(Calendar.HOUR, 23);
                calTo.set(Calendar.MINUTE, 59);
                calTo.set(Calendar.SECOND, 59);
                toDate = calTo.getTime();
                break;
            }
        }
        return new Date[]{fromDate, toDate};
    }

    public static ArrayList<Date> generateTimeKey(Date fromDate, Date toDate, int periodIndex) {
        Date tempDate = fromDate;
        ArrayList<Date> timePeriods = new ArrayList<>();

        while (true) {
            timePeriods.add(tempDate);
            Calendar cal = Calendar.getInstance();
            cal.setTime(tempDate);
            switch (periodIndex) {
                case 0: {
                    cal.add(Calendar.HOUR, 1);
                    break;
                }
                case 1: {
                    cal.add(Calendar.DATE, 1);
                    break;
                }
                case 2: {
                    cal.add(Calendar.DATE, 7);
                    break;
                }
                case 3: {
                    cal.add(Calendar.MONTH, 1);
                    break;
                }
                default: {
                    cal.add(Calendar.DATE, 1);
                    break;
                }
            }
            tempDate = cal.getTime();
            if (tempDate.after(toDate)) {
                break;
            }
        }
        return timePeriods;
    }

    public static String formatCurrency(double number) {
        return String.valueOf(number);
    }

    public static int getLastDayOfMonth(int month, int year) {
        switch (month) {
            case 1:
            case 3:
            case 5:
            case 7:
            case 8:
            case 10:
            case 12:
                return 31;
            case 4:
            case 6:
            case 9:
            case 11:
                return 30;
            case 2:
                if ((year % 4 == 0 && year % 100 != 0) || year % 400 == 0) {
                    return 29;
                } else {
                    return 28;
                }
            default:
                return 0;
        }
    }
